import 'dart:convert';

import 'package:chia_crypto_utils/chia_crypto_utils.dart';
import 'package:chia_crypto_utils/src/did/models/uncurried_did_puzzle.dart';

void main() {
  final spendBundle = SpendBundle.fromJson(
    jsonDecode(
      '{"coin_spends":[{"coin":{"parent_coin_info":"0x9c8d46a7a938114e4620c44ead2514c6b6c33a871af2e8cda2b36cab183c612c","puzzle_hash":"0x2bf96ec9e90b164de8b303958fe5e32fbbd777e14dd4a2587e4b0c2c450df988","amount":1},"puzzle_reveal":"","solution":""}],"aggregated_signature":null}',
    ) as Map<String, dynamic>,
  );

  final privateKey =
      PrivateKey.fromHex('0ebdfc43fb7be1f78a50782ed76251520de6686b80e645f0b4e658868a9e792f');

  final fullDidPuzzle = spendBundle.coinSpends.single.puzzleReveal;

  final p2FromDriver =
      PuzzleDriver.match(fullDidPuzzle)!.getP2Puzzle(spendBundle.coinSpends.single).hash();

  print(p2FromDriver);

  final uncurriedPuzzle = UncurriedDidPuzzle.fromProgram(fullDidPuzzle);

  print(uncurriedPuzzle.innerPuzzle.p2Puzzle.hash());

  print(getPuzzleFromPk(privateKey.getG1()).hash());
}
